import React, { Component} from "react";
import { Button, Container, Form, FormGroup, Input } from 'reactstrap';
import AuthService from "../../authentication/auth.service";

export default class RegisterWorkDesc extends Component {
    constructor(props) {
    super(props);
    this.handleWorkDesc = this.handleWorkDesc.bind(this);
    this.onChangeWorkAmt = this.onChangeWorkAmt.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.onChangeLocation = this.onChangeLocation.bind(this);
    this.onChangeCategory = this.onChangeCategory.bind(this);
    
    this.state = {
      workAmount: "",
      description: "",
      location: "",
      category: "",
      successful: false,
      message: "",
    };
  }

//   componentDidMount() {
//     const currentUser = AuthService.getCurrentUser();
//     const id=currentUser.id;
//     this.setState.id=id;
//   }

  onChangeWorkAmt(e) {
    this.setState({
      workAmount: e.target.value,
    });
  }

  onChangeDescription(e) {
    this.setState({
        description: e.target.value
    });
  }

  onChangeLocation(e) {
    this.setState({
      location: e.target.value
    });
  }

  onChangeCategory(e) {
    this.setState({
        category: e.target.value
    });
  }

  
  handleRegister(e) {
    e.preventDefault();

    this.setState({
      message: "",
      successful: false,
    });

    this.form.validateAll();

    if (this.checkBtn.context._errors.length === 0) {
      AuthService.addWorkDesc(
        this.state.workAmount,
        this.state.description,
        this.state.location,
        this.state.category
      ).then(
        response => {
          this.setState({
            message: response.data.message,
            successful: true
          });
        },
        error => {
          const resMessage =
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString();

          this.setState({
            successful: false,
            message: resMessage
          });
        }
      );
    }
  }

  render() {
    return (
        <div>
            <Container style={{ paddingLeft: '300px' }}>
                <h1 className="text-center my-3">Add Work Description</h1>
                <Form onSubmit={this.handleRegister}>
                    <FormGroup>
                        <label for="amount"> Work Amount</label>
                        <Input required type="text" placeholder="Work Amount" name="amount" id="amt"
                            onChange={this.onChangeWorkAmt} />
                    </FormGroup>
                    <FormGroup>
                        <label for="work">Work Description</label>
                        <Input required type="text" placeholder="Work Description" name="desc" id="desc" onChange={this.onChangeDescription} />
                    </FormGroup>
                    <FormGroup>
                        <label for="exampleSelect">Work Category</label>
                        <Input required type="select" name="CUSTOMER" id="CUSTOMER" onChange={this.onChangeCategory} >
                            <option></option>
                            <option>PLUMBER</option>
                            <option>ELECTRICIAN</option>
                            <option>MECHANIC</option>
                            <option>CARPENTER</option>
                            <option>AUTOMOTIVE_TECHNICIAN</option>
                            <option>WELDER</option>
                            <option>PAINTER</option>
                            <option>HOUSE_CLEANING</option>
                            <option>ELECTRONIC_REPAIRER</option>
                        </Input>
                    </FormGroup>
                    <FormGroup>

                        <label for="exampleSelect">City</label>
                        <Input required type="select" name="city" id="city" onChange={this.onChangeLocation} >
                            <option></option>
                            <option>PUNE</option>
                            <option>MUMBAI</option>
                            <option>NAGPUR</option>
                            <option>BHOPAL</option>
                            <option>INDORE</option>
                            <option>AHMEDABAD</option>
                            <option>JAIPUR</option>
                            <option>NOIDA</option>
                            <option>BANGALORE</option>
                        </Input>
                    </FormGroup>

                    <Container className="text-center">
                        <Button color="success" type="submit">Add</Button>
                        <Button color="warning ml-3" type="reset">Clear</Button>
                    </Container>
                </Form>
            </Container>
        </div>
    )
  }
}
